    //   };

    return(

        <>
          
          <h1>{startingH1}</h1>

  <div class="dice">
    <p>Player 1</p>
    <img class="img1" name="diceimg1" src={startingDice1} />
  </div>

  <div class="dice">
    <p>Player 2</p>
    <img class="img2" name="diceimg2" src={startingDice2} />
  </div>

  </>

          )    
})};

export default App
