import React, { useState } from "react"

function App(){

    const random1 = Math.round(Math.random()*5+1);
    const random2 = Math.round(Math.random()*5+1);

    const [startingDice1, setDice1] = useState("images/dice6.png");
    const [startingDice2, setDice2] = useState("images/dice6.png");
    const [startingH1, setH1] = useState("Refresh to roll!");

    if (random1 > random2) {
        setH1 = "Player 1 Wins"
      }
      if (random1 < random2) {
        setH1 = "Player 2 Wins"
      }else if (random1 === random2){
        setH1 = "Tis A Draw!"
      };

    return(

        <>
          
          <h1>{startingH1}</h1>

  <div class="dice">
    <p>Player 1</p>
    <img class="img1" name="diceimg1" src={startingDice1} />
  </div>

  <div class="dice">
    <p>Player 2</p>
    <img class="img2" name="diceimg2" src={startingDice2} />
  </div>

  </>

          )    
};

export default App;
